class Key
  KEYPAD = [
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9]
  ]
  attr_accessor :x, :y
  def initialize(x, y)
    self.x = x
    self.y = y
  end
  def key
    KEYPAD[y][x]
  end
end

def find_key(start_key, instructions)
  x, y = start_key.x, start_key.y
  instructions.each_char do |chr|
    case chr
    when "U"
      y = [y - 1, 0].max
    when "R"
      x = [x + 1, 2].min
    when "D"
      y = [y + 1, 2].min
    when "L"
      x = [x - 1, 0].max
    end
  end

  Key.new(x, y)
end

