class Key
  KEYPAD = [
    [nil, nil,  1 , nil, nil],
    [nil,  2 ,  3 ,  4 , nil],
    [ 5 ,  6 ,  7 ,  8 ,  9 ],
    [nil, "A", "B", "C", nil],
    [nil, nil, "D", nil, nil]
  ]
  attr_accessor :x, :y
  def initialize(x, y)
    self.x = x
    self.y = y
  end
  def key
    KEYPAD[y][x]
  rescue => e
    puts "x: #{x}, y: #{y}"
    raise e
  end
  def valid?
    !key.nil?
  end
end

def find_key(start_key, instructions)
  x, y = start_key.x, start_key.y

  instructions.each_char do |chr|
    possible_x, possible_y = x, y
    case chr
    when "U"
      possible_y = [y - 1, 0].max
    when "R"
      possible_x = [x + 1, 4].min
    when "D"
      possible_y = [y + 1, 4].min
    when "L"
      possible_x = [x - 1, 0].max
    end

    if Key.new(possible_x, possible_y).valid?
      x = possible_x
      y = possible_y
    end
  end

  Key.new(x, y)
end

